mapper.select:
SELECT B.* FROM( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT BOARD.* FROM BOARD ORDER BY ROWID DESC) A
) b
WHERE RNUM BETWEEN #{startRow} AND #{endRow}

mapper.update:
UPDATE BOARD


			BO_MAIL 	= #{bo_mail},


			BO_TITLE 	= #{bo_title},


			BO_CONTENT 	= #{bo_content}


WHERE
BO_NO =#{bo_no}
